mapper.update:
UPDATE shop SET ban_time = #{banTime}, violation = #{violation},
                update_time = CURRENT_TIMESTAMP
 WHERE id = #{id}

UPDATE shop SET ${field2} = #{value2},
                update_time = CURRENT_TIMESTAMP
 WHERE ${field1} = #{value1};

mapper.select:
SELECT s.id,s.owner_id,s.name AS shop_name, s.heat,s.violation,s.create_time,s.ban_time, mi.name AS real_name, c.name AS city_name FROM shop AS s LEFT JOIN merchant_info mi ON s.owner_id = mi.user_id LEFT JOIN city c ON s.city_id = c.id
AND (s.id = #{key} OR s.owner_id = #{key} OR s.name like #{toKey})
AND s.create_time BETWEEN #{start} AND #{end}
AND (c.id = #{cityId} OR c.parent_id = #{cityId})
LIMIT 10 OFFSET #{cur}
